import React, { useState } from "react";
import axios from "axios";
import Sidebar from "../Sidebar";
import Header from "../Header";
import Footer from "../Footer";

const AddSubject = () => {
  const [formData, setFormData] = useState({
    courseName: "",
    courseDescription: "",
    price: "",
    tag: "",
    availableInstructors: [],
    thumbnail: null,
    whatYouWillLearn: "",
    courseContent: "",
    instructions: "",
    status: "active",
  });

  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, thumbnail: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation
    if (formData.price <= 0) {
      setError("Price must be a positive number.");
      return;
    }

    if (formData.tag.length < 3) {
      setError("Tag must be at least 3 characters long.");
      return;
    }

    setLoading(true);
    setError("");
    setSuccessMessage("");

    try {
      const token = localStorage.getItem("authToken");

      // Prepare FormData for file upload
      const data = new FormData();
      Object.keys(formData).forEach((key) => {
        data.append(key, formData[key]);
      });

      const response = await axios.post(
        "http://localhost:4000/api/v1/courses/add",
        data,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setSuccessMessage("Course added successfully!");
      setFormData({
        courseName: "",
        courseDescription: "",
        price: "",
        tag: "",
        availableInstructors: [],
        thumbnail: null,
        whatYouWillLearn: "",
        courseContent: "",
        instructions: "",
        status: "active",
      });
    } catch (error) {
      setError(
        error.response?.data?.message || "An error occurred while adding the course."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 overflow-hidden">
      <Sidebar />

      <div className="flex-1 flex flex-col overflow-hidden">
        <Header />

        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-200 p-4">
          <div className="container mx-auto max-w-7xl">
            <div className="mb-6">
              <h1 className="text-2xl font-semibold">Add New Course</h1>
              <p className="text-sm text-gray-600">
                Fill in the form below to add a new course.
              </p>
            </div>

            {error && <p className="text-red-500">{error}</p>}
            {successMessage && <p className="text-green-500">{successMessage}</p>}

            <form
              onSubmit={handleSubmit}
              className="bg-white shadow-md rounded-lg p-6 space-y-4"
            >
              <div className="flex flex-col">
                <label htmlFor="courseName" className="text-gray-600 text-sm mb-1">
                  Course Name
                </label>
                <input
                  type="text"
                  id="courseName"
                  name="courseName"
                  value={formData.courseName}
                  onChange={handleChange}
                  placeholder="Enter course name"
                  className="border border-gray-300 rounded-lg p-2"
                  required
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="courseDescription" className="text-gray-600 text-sm mb-1">
                  Course Description
                </label>
                <textarea
                  id="courseDescription"
                  name="courseDescription"
                  value={formData.courseDescription}
                  onChange={handleChange}
                  placeholder="Enter course description"
                  className="border border-gray-300 rounded-lg p-2"
                  required
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="price" className="text-gray-600 text-sm mb-1">
                  Price
                </label>
                <input
                  type="number"
                  id="price"
                  name="price"
                  value={formData.price}
                  onChange={handleChange}
                  placeholder="Enter price"
                  className="border border-gray-300 rounded-lg p-2"
                  required
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="tag" className="text-gray-600 text-sm mb-1">
                  Tag
                </label>
                <input
                  type="text"
                  id="tag"
                  name="tag"
                  value={formData.tag}
                  onChange={handleChange}
                  placeholder="Enter tag"
                  className="border border-gray-300 rounded-lg p-2"
                  required
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="thumbnail" className="text-gray-600 text-sm mb-1">
                  Thumbnail
                </label>
                <input
                  type="file"
                  id="thumbnail"
                  name="thumbnail"
                  onChange={handleFileChange}
                  className="border border-gray-300 rounded-lg p-2"
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="whatYouWillLearn" className="text-gray-600 text-sm mb-1">
                  What You Will Learn
                </label>
                <textarea
                  id="whatYouWillLearn"
                  name="whatYouWillLearn"
                  value={formData.whatYouWillLearn}
                  onChange={handleChange}
                  placeholder="Enter what students will learn"
                  className="border border-gray-300 rounded-lg p-2"
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="courseContent" className="text-gray-600 text-sm mb-1">
                  Course Content
                </label>
                <textarea
                  id="courseContent"
                  name="courseContent"
                  value={formData.courseContent}
                  onChange={handleChange}
                  placeholder="Enter course content"
                  className="border border-gray-300 rounded-lg p-2"
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="instructions" className="text-gray-600 text-sm mb-1">
                  Instructions
                </label>
                <textarea
                  id="instructions"
                  name="instructions"
                  value={formData.instructions}
                  onChange={handleChange}
                  placeholder="Enter course instructions"
                  className="border border-gray-300 rounded-lg p-2"
                />
              </div>

              <button
                type="submit"
                className="bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition"
                disabled={loading}
              >
                {loading ? "Submitting..." : "Add Course"}
              </button>
            </form>
          </div>
        </main>

        <Footer />
      </div>
    </div>
  );
};

export default AddSubject;
