import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function AddSubject() {
  const [subjectName, setSubjectName] = useState("");
  const [subjectCode, setSubjectCode] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();

  const handleAddSubject = async (event) => {
    event.preventDefault();

    // Retrieve the token from localStorage
    const token = localStorage.getItem("token");

    if (!token) {
      setErrorMessage("You must be logged in to add a subject.");
      return;
    }

    try {
      // Send request to add subject
      const response = await axios.post(
        "http://localhost:4000/api/v1/subjects", // Example endpoint
        {
          subjectName,
          subjectCode,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`, // Include the token in headers
          },
        }
      );

      // Handle successful response
      setSuccessMessage("Subject added successfully!");
      setSubjectName("");
      setSubjectCode("");
    } catch (error) {
      // Handle errors
      setErrorMessage(
        error.response?.data?.message || "Failed to add the subject."
      );
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md w-full max-w-md">
        <h1 className="text-xl font-bold mb-6">Add Subject</h1>
        {errorMessage && <p className="text-red-500 mb-4">{errorMessage}</p>}
        {successMessage && <p className="text-green-500 mb-4">{successMessage}</p>}
        <form onSubmit={handleAddSubject}>
          <div className="mb-4">
            <label className="block mb-2 text-sm font-medium text-gray-700">
              Subject Name
            </label>
            <input
              type="text"
              value={subjectName}
              onChange={(e) => setSubjectName(e.target.value)}
              required
              className="w-full p-2 border rounded focus:outline-none focus:ring"
            />
          </div>
          <div className="mb-4">
            <label className="block mb-2 text-sm font-medium text-gray-700">
              Subject Code
            </label>
            <input
              type="text"
              value={subjectCode}
              onChange={(e) => setSubjectCode(e.target.value)}
              required
              className="w-full p-2 border rounded focus:outline-none focus:ring"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition"
          >
            Add Subject
          </button>
        </form>
      </div>
    </div>
  );
}
