import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AdminDashboard = () => {
  const [data, setData] = useState(null);
  const navigate = useNavigate();

  // Fetch admin data or perform any actions you want on the dashboard
  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");

      if (!token) {
        navigate("/"); // Redirect to login if token doesn't exist
        return;
      }

      try {
        // Replace the URL with the appropriate endpoint for the admin data
        const response = await axios.get("http://localhost:4000/api/v1/admin/data", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setData(response.data); // Set fetched data to state
      } catch (error) {
        console.error("Error fetching data:", error.message);
        navigate("/"); // Redirect to login if there was an error or no access
      }
    };

    fetchData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token"); // Remove token on logout
    navigate("/"); // Redirect to login page
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="container mx-auto">
        <h1 className="text-3xl font-semibold text-center mb-6">Admin Dashboard</h1>

        {/* Display the fetched data or loading message */}
        {data ? (
          <div>
            <h2 className="text-xl font-semibold">Admin Data:</h2>
            <pre>{JSON.stringify(data, null, 2)}</pre>
          </div>
        ) : (
          <p>Loading admin data...</p>
        )}

        {/* Logout Button */}
        <div className="text-center mt-6">
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white px-4 py-2 rounded-md"
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
