import React, { useState } from "react";

const AddCourse = () => {
  const [courseName, setCourseName] = useState("");
  const [courseDescription, setCourseDescription] = useState("");
  const [courseDuration, setCourseDuration] = useState("");

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Create a course object
    const newCourse = {
      name: courseName,
      description: courseDescription,
      duration: courseDuration,
    };

    // Normally here you would send `newCourse` to the API or save it in state.
    console.log("New Course Added:", newCourse);

    // Reset the form
    setCourseName("");
    setCourseDescription("");
    setCourseDuration("");
  };

  return (
    <div className="bg-white p-8 rounded-md shadow-md w-1/2 mx-auto">
      <h2 className="text-2xl font-semibold text-center mb-6">Add New Course</h2>
      
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="courseName" className="block text-gray-700">Course Name</label>
          <input
            type="text"
            id="courseName"
            value={courseName}
            onChange={(e) => setCourseName(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="courseDescription" className="block text-gray-700">Course Description</label>
          <textarea
            id="courseDescription"
            value={courseDescription}
            onChange={(e) => setCourseDescription(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="courseDuration" className="block text-gray-700">Course Duration (weeks)</label>
          <input
            type="number"
            id="courseDuration"
            value={courseDuration}
            onChange={(e) => setCourseDuration(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
            required
          />
        </div>

        <div className="text-center">
          <button type="submit" className="bg-blue-500 text-white px-6 py-2 rounded-md">
            Add Course
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddCourse;
