import React from "react";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom"; // Import Link for navigation

const AdminDashboard = () => {
  const navigate = useNavigate();

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem("token"); // Remove token on logout
    navigate("/"); // Redirect to login page
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="container mx-auto">
        <h1 className="text-3xl font-semibold text-center mb-6">Admin Dashboard</h1>

        {/* Navigation Links */}
        <div className="text-center mb-6">
          <h3 className="text-xl font-medium">Manage Your Admin Tasks</h3>
          <div className="mt-4">
            <Link to="/dashboard/admin/add-tutor" className="text-blue-500 mr-4">
              Add Tutor
            </Link>
            <Link to="/dashboard/admin/list-courses" className="text-blue-500 mr-4">
              List Courses
            </Link>
            <Link to="/dashboard/admin/list-tutors" className="text-blue-500">
              List Tutors
            </Link>
          </div>
        </div>

        {/* Logout Button */}
        <div className="text-center mt-6">
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white px-4 py-2 rounded-md"
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
