import React, { useState } from "react";
import axios from "axios";

const AddCourse = () => {
  const [courseDetails, setCourseDetails] = useState({
    courseName: "",
    courseDescription: "",
    availableInstructors: [],
    whatYouWillLearn: "",
    courseContent: "",
    price: "",
    thumbnail: "",
    tag: "",
    instructions: "",
    status: "",
  });

  const [loading, setLoading] = useState(false); // For loading state
  const [error, setError] = useState(null); // For error handling
  const [success, setSuccess] = useState(""); // For success message

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCourseDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess("");

    // Get token from localStorage
    const token = localStorage.getItem("token");

    if (!token) {
      setError("Authentication required. Please log in.");
      setLoading(false);
      return;
    }

    try {
      // Sending POST request to add course
      const response = await axios.post(
        "http://localhost:4000/api/v1/courses/add",
        courseDetails, // Sending the entire courseDetails object
        {
          headers: {
            Authorization: `Bearer ${token}`, // Passing token in Authorization header
          },
        }
      );

      setSuccess("Course added successfully!");
      setCourseDetails({}); // Reset form
    } catch (error) {
      setError(error.response ? error.response.data.message : "An error occurred.");
    } finally {
      setLoading(false); // Stop loading state
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 overflow-hidden">


      <div className="flex-1 flex flex-col overflow-hidden">
        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-200 p-4">
          <div className="container mx-auto max-w-7xl">
            <div className="mb-6">
              <h1 className="text-2xl font-semibold">Add New Course</h1>
              <p className="text-sm text-gray-600">
                Fill in the form below to add a new course.
              </p>
            </div>

            <form
              onSubmit={handleSubmit}
              className="bg-white shadow-md rounded-lg p-6 space-y-4"
            >
              <div className="flex flex-col">
                <label htmlFor="courseName" className="text-gray-600 text-sm mb-1">
                  Course Name
                </label>
                <input
                  type="text"
                  id="courseName"
                  name="courseName"
                  value={courseDetails.courseName}
                  onChange={handleChange}
                  placeholder="Enter course name"
                  className="border border-gray-300 rounded-lg p-2"
                  required
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="courseDescription" className="text-gray-600 text-sm mb-1">
                  Course Description
                </label>
                <textarea
                  id="courseDescription"
                  name="courseDescription"
                  value={courseDetails.courseDescription}
                  onChange={handleChange}
                  placeholder="Enter course description"
                  className="border border-gray-300 rounded-lg p-2"
                  required
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="availableInstructors" className="text-gray-600 text-sm mb-1">
                  Available Instructors (Comma Separated IDs)
                </label>
                <input
                  type="text"
                  id="availableInstructors"
                  name="availableInstructors"
                  value={courseDetails.availableInstructors}
                  onChange={handleChange}
                  placeholder="Enter instructor IDs"
                  className="border border-gray-300 rounded-lg p-2"
                  required
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="whatYouWillLearn" className="text-gray-600 text-sm mb-1">
                  What You Will Learn
                </label>
                <textarea
                  id="whatYouWillLearn"
                  name="whatYouWillLearn"
                  value={courseDetails.whatYouWillLearn}
                  onChange={handleChange}
                  placeholder="Enter what you will learn"
                  className="border border-gray-300 rounded-lg p-2"
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="courseContent" className="text-gray-600 text-sm mb-1">
                  Course Content
                </label>
                <textarea
                  id="courseContent"
                  name="courseContent"
                  value={courseDetails.courseContent}
                  onChange={handleChange}
                  placeholder="Enter course content"
                  className="border border-gray-300 rounded-lg p-2"
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="price" className="text-gray-600 text-sm mb-1">
                  Price
                </label>
                <input
                  type="number"
                  id="price"
                  name="price"
                  value={courseDetails.price}
                  onChange={handleChange}
                  placeholder="Enter price"
                  className="border border-gray-300 rounded-lg p-2"
                  required
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="thumbnail" className="text-gray-600 text-sm mb-1">
                  Thumbnail URL
                </label>
                <input
                  type="text"
                  id="thumbnail"
                  name="thumbnail"
                  value={courseDetails.thumbnail}
                  onChange={handleChange}
                  placeholder="Enter thumbnail URL"
                  className="border border-gray-300 rounded-lg p-2"
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="tag" className="text-gray-600 text-sm mb-1">
                  Tag
                </label>
                <input
                  type="text"
                  id="tag"
                  name="tag"
                  value={courseDetails.tag}
                  onChange={handleChange}
                  placeholder="Enter course tag"
                  className="border border-gray-300 rounded-lg p-2"
                  required
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="instructions" className="text-gray-600 text-sm mb-1">
                  Instructions
                </label>
                <textarea
                  id="instructions"
                  name="instructions"
                  value={courseDetails.instructions}
                  onChange={handleChange}
                  placeholder="Enter course instructions"
                  className="border border-gray-300 rounded-lg p-2"
                />
              </div>

              <div className="flex flex-col">
                <label htmlFor="status" className="text-gray-600 text-sm mb-1">
                  Status
                </label>
                <select
                  id="status"
                  name="status"
                  value={courseDetails.status}
                  onChange={handleChange}
                  className="border border-gray-300 rounded-lg p-2"
                >
                  <option value="draft">Draft</option>
                  <option value="published">Published</option>
                </select>
              </div>

              {error && (
                <p className="text-red-500 text-sm">{error}</p>
              )}

              {success && (
                <p className="text-green-500 text-sm">{success}</p>
              )}

              <button
                type="submit"
                className="bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition"
                disabled={loading} // Disable button when loading
              >
                {loading ? "Adding..." : "Add Course"}
              </button>
            </form>
          </div>
        </main>
      </div>
    </div>
  );
};

export default AddCourse;
