import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddCourse = () => {
  const navigate = useNavigate();

  // Set initial form state
  const [courseName, setCourseName] = useState("");
  const [courseDescription, setCourseDescription] = useState("");
  const [price, setPrice] = useState("");
  const [tag, setTag] = useState([]);
  const [category, setCategory] = useState("");
  const [status, setStatus] = useState("Draft");
  const [error, setError] = useState("");

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (price <= 0) {
      setError("Price must be greater than zero.");
      return;
    }

    const courseData = {
      courseName,
      courseDescription,
      price,
      tag,
      category,
      status,
    };

    try {
      const token = localStorage.getItem("token"); // Retrieve the token from localStorage
      console.log(token); // Log the token to ensure it's being retrieved
      if (!token) {
        setError("No token found. Please log in.");
        return;
      }

      // Post the data to your API with the token included in the Authorization header
      const response = await axios.post(
        "http://localhost:4000/api/v1/courses/add",
        courseData,
        {
          headers: {
            Authorization: `Bearer ${token}`, // Add token to Authorization header
          },
        }
      );

      alert("Course added successfully!");
      navigate("/dashboard/admin"); // Redirect to the admin dashboard after successful creation
    } catch (error) {
      console.error("Error adding course:", error);
      const errorMessage = error.response?.data?.message || "Failed to add course.";
      setError(errorMessage); // Set the error message to be displayed
      alert(errorMessage); // Optionally show an alert to the user
    }
  };

  return (
    <div className="add-course-container">
      <h2>Add a New Course</h2>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          Course Name:
          <input
            type="text"
            value={courseName}
            onChange={(e) => setCourseName(e.target.value)}
          />
        </label>
        <label>
          Course Description:
          <textarea
            value={courseDescription}
            onChange={(e) => setCourseDescription(e.target.value)}
          />
        </label>
        <label>
          Price:
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </label>
        <label>
          Tags:
          <input
            type="text"
            value={tag}
            onChange={(e) => setTag(e.target.value.split(","))}
          />
        </label>
        <label>
          Category:
          <input
            type="text"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />
        </label>
        <label>
          Status:
          <select value={status} onChange={(e) => setStatus(e.target.value)}>
            <option value="Draft">Draft</option>
            <option value="Published">Published</option>
          </select>
        </label>
        <button type="submit">Add Course</button>
      </form>
    </div>
  );
};

export default AddCourse;
