import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddCourse = () => {
  const navigate = useNavigate();

  // Set initial form state
  const [courseName, setCourseName] = useState("");
  const [courseDescription, setCourseDescription] = useState("");
  const [whatYouWillLearn, setWhatYouWillLearn] = useState("");
  const [price, setPrice] = useState("");
  const [thumbnail, setThumbnail] = useState("");
  const [tag, setTag] = useState([]);
  const [category, setCategory] = useState("");
  const [studentsEnrolled, setStudentsEnrolled] = useState([]);
  const [instructions, setInstructions] = useState([]);
  const [status, setStatus] = useState("Draft");
  const [availableInstructors, setAvailableInstructors] = useState([]);
  const [courseContent, setCourseContent] = useState([]);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const courseData = {
      courseName,
      courseDescription,
      whatYouWillLearn,
      price,
      thumbnail,
      tag,
      category,
      studentsEnrolled,
      instructions,
      status,
      availableInstructors,
      courseContent,
    };

    try {
      // Post the data to your API
      await axios.post("http://localhost:4000/api/v1/courses/add", courseData); // Your API URL
      alert("Course added successfully!");
      navigate("/dashboard/admin"); // Redirect to the admin dashboard after successful creation
    } catch (error) {
      console.error("Error adding course:", error);
      alert("Failed to add course.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="container mx-auto">
        <h1 className="text-3xl font-semibold text-center mb-6">Add New Course</h1>

        <form onSubmit={handleSubmit} className="max-w-lg mx-auto space-y-4">
          <div>
            <label htmlFor="courseName" className="block text-lg font-medium">
              Course Name
            </label>
            <input
              type="text"
              id="courseName"
              value={courseName}
              onChange={(e) => setCourseName(e.target.value)}
              className="w-full p-2 border rounded-md"
              required
            />
          </div>

          <div>
            <label htmlFor="courseDescription" className="block text-lg font-medium">
              Course Description
            </label>
            <textarea
              id="courseDescription"
              value={courseDescription}
              onChange={(e) => setCourseDescription(e.target.value)}
              className="w-full p-2 border rounded-md"
              required
            />
          </div>

          <div>
            <label htmlFor="whatYouWillLearn" className="block text-lg font-medium">
              What You Will Learn
            </label>
            <textarea
              id="whatYouWillLearn"
              value={whatYouWillLearn}
              onChange={(e) => setWhatYouWillLearn(e.target.value)}
              className="w-full p-2 border rounded-md"
              required
            />
          </div>

          <div>
            <label htmlFor="price" className="block text-lg font-medium">
              Price
            </label>
            <input
              type="number"
              id="price"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              className="w-full p-2 border rounded-md"
              required
            />
          </div>

          <div>
            <label htmlFor="thumbnail" className="block text-lg font-medium">
              Course Thumbnail URL
            </label>
            <input
              type="text"
              id="thumbnail"
              value={thumbnail}
              onChange={(e) => setThumbnail(e.target.value)}
              className="w-full p-2 border rounded-md"
            />
          </div>

          <div>
            <label htmlFor="tag" className="block text-lg font-medium">
              Tags (comma separated)
            </label>
            <input
              type="text"
              id="tag"
              value={tag.join(", ")}
              onChange={(e) => setTag(e.target.value.split(",").map((item) => item.trim()))}
              className="w-full p-2 border rounded-md"
              required
            />
          </div>

          <div>
            <label htmlFor="category" className="block text-lg font-medium">
              Category
            </label>
            <select
              id="category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="w-full p-2 border rounded-md"
            >
              {/* Example categories, adjust as per your setup */}
              <option value="">Select Category</option>
              <option value="605b2b3b2d3c3c3f0c4c5c6d">Web Development</option>
              <option value="605b2b3b2d3c3c3f0c4c5c6e">Data Science</option>
              {/* Add more options as needed */}
            </select>
          </div>

          <div>
            <label htmlFor="availableInstructors" className="block text-lg font-medium">
              Available Instructors (comma separated User IDs)
            </label>
            <input
              type="text"
              id="availableInstructors"
              value={availableInstructors.join(", ")}
              onChange={(e) =>
                setAvailableInstructors(e.target.value.split(",").map((item) => item.trim()))
              }
              className="w-full p-2 border rounded-md"
            />
          </div>

          <div>
            <label htmlFor="courseContent" className="block text-lg font-medium">
              Course Content (comma separated Section IDs)
            </label>
            <input
              type="text"
              id="courseContent"
              value={courseContent.join(", ")}
              onChange={(e) =>
                setCourseContent(e.target.value.split(",").map((item) => item.trim()))
              }
              className="w-full p-2 border rounded-md"
            />
          </div>

          <div>
            <label htmlFor="instructions" className="block text-lg font-medium">
              Instructions (comma separated)
            </label>
            <input
              type="text"
              id="instructions"
              value={instructions.join(", ")}
              onChange={(e) =>
                setInstructions(e.target.value.split(",").map((item) => item.trim()))
              }
              className="w-full p-2 border rounded-md"
            />
          </div>

          <div>
            <label htmlFor="status" className="block text-lg font-medium">
              Status
            </label>
            <select
              id="status"
              value={status}
              onChange={(e) => setStatus(e.target.value)}
              className="w-full p-2 border rounded-md"
            >
              <option value="Draft">Draft</option>
              <option value="Published">Published</option>
            </select>
          </div>

          <div className="text-center mt-6">
            <button
              type="submit"
              className="bg-blue-500 text-white px-4 py-2 rounded-md"
            >
              Add Course
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddCourse;
